mapper.select:
SELECT COUNT(1) FROM business b INNER JOIN sell_type AS s ON s.id = b.sell_type_id INNER JOIN employee AS e ON e.id = b.emp_id
DATE(sell_date) LIKE BINARY CONCAT('%',#{businessDate},'%')
AND sell_type_id = #{sellTypeId}

mapper.update:
update business
set price = #{price,jdbcType=INTEGER}
